import React, { useEffect, useState } from "react";
import NewsItem from "./NewsItem";
import Spinner from "./Spinner";
import InfiniteScroll from "react-infinite-scroll-component";

const News = (props) => {
  const capitalize = (string) => {
    return string.charAt(0).toUpperCase() + string.slice(1);
  };

  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);

  const [page, setPage] = useState("");
  const [totalResults, setTotalResults] = useState(0);

  const updateNews = async () => {
    props.setProgress(10);
    let url = `https://newsdata.io/api/1/news?apikey=pub_299652a525c423835907260c3d22b0018a182&country=in&language=en&category=${props.category}`;
    // let url = `https://newsapi.org/v2/top-headlines?&country=in&apikey=${props.apiKey}&category=${props.category}&page=${page}&pageSize=${props.pageSize}`;
    setLoading(true);
    let data = await fetch(url);
    props.setProgress(30);
    let parsedData = await data.json();
    props.setProgress(60);
    setResults(parsedData.results);
    setPage(parsedData.nextPage);
    setTotalResults(parsedData.totalResults);
    setLoading(false);
    props.setProgress(100);
  };
  useEffect(() => {
    document.title = `${capitalize(props.category)}  - News Reaper`;
    updateNews();
    //eslint-disable-next-line
  }, []);

  const fetchData = async () => {
    let url = `https://newsdata.io/api/1/news?apikey=pub_299652a525c423835907260c3d22b0018a182&country=in&language=en&category=${
      props.category
    }&page=${page}`;
    setPage(page+1);
    let data = await fetch(url);
    let parsedData = await data.json();
    setResults(results.concat(parsedData.results));
    setTotalResults(parsedData.totalResults);
  };
  return (
    <>
      <h1 className="text-center" style={{ margin: "90px 0px 10px 0px" }}>
        News Reaper - Top {capitalize(props.category)} Headlines
      </h1>
      {loading && <Spinner />}
      <InfiniteScroll
        dataLength={results.length}
        next={fetchData}
        hasMore={results.length !== totalResults}
        loader={<Spinner />}
      >
        <div className="container my-2">
          <div className="row my-3 ">
            {results.map((element) => {
              return (
                <div className="col-md-4 my-3" key={element.link}>
                  <NewsItem
                    title={element.title ? element.title : ""}
                    description={
                      element.description
                        ? element.description.slice(0, 200)
                        : ""
                    }
                    newsUrl={element.link}
                    imageUrl={
                      element.image_url
                        ? element.image_url
                        : "https://thumbs.dreamstime.com/b/newspaper-wooden-table-93401143.jpg"
                    }
                    creator={element.creator ? element.creator : "Unknown"}
                    date={element.pubDate}
                    source={element.source_id}
                  />
                </div>
              );
            })}
          </div>
        </div>
      </InfiniteScroll>
    </>
  );
};
export default News;
