import React ,{useEffect, useState} from "react";
import NewsItem from "./NewsItem";
import Spinner from "./Spinner";
import InfiniteScroll from "react-infinite-scroll-component";

const News = (props) => {
 const capitalize = (string) => {
    return string.charAt(0).toUpperCase() + string.slice(1);
  };

  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(1);
  const [totalResults, setTotalResults] = useState(0);

  // document.title = `${this.capitalize(props.category)}  - News Reaper`;
};
const updateNews = async () => {
  // setProgress(10);
   props.setProgress(10);
  let url = `https://newsapi.org/v2/top-headlines?&country=in&apikey=${props.apiKey}&category=${props.category}&page=${page}&pageSize=${props.pageSize}`;
  setLoading(true);
  // this.setState({ loading: true });
  let data = await fetch(url);
  // setProgress(30);
  props.setProgress(30);
  let parsedData = await data.json();
  // setProgress(60);
  props.setProgress(60);
  setArticles(parsedData.articles);
  setTotalResults(parsedData.totalResults);
  setLoading(false);
  // this.setState({
  //   articles: parsedData.articles,
  //   totalResults: parsedData.totalResults,
  //   loading: false,
  // });
  props.setProgress(100);

  const useEffect = (() => {
    this.updateNews();
  }, []);

 const fetchData = async () => {
  setPage(page+1)
    // this.setState({ page: this.state.page + 1 });
    let url = `https://newsapi.org/v2/top-headlines?&country=in&apikey=${
      props.apiKey
    }&category=${props.category}&page=${page + 1}&pageSize=${
      props.pageSize
    }`;
    let data = await fetch(url);
    let parsedData = await data.json();
    setArticles(articles.concat(parsedData.articles))
    setTotalResults(parsedData.totalResults)
    // this.setState({
    //   articles: this.state.articles.concat(parsedData.articles),
    //   totalResults: parsedData.totalResults,
    // });
  };
  return (
    <>
      <h1 className="text-center" style={{ margin: "30px 0" }}>
        News Reaper - Top {capitalize(props.category)} Headlines
      </h1>
      {loading && <Spinner />}
      <InfiniteScroll
        dataLength={articles.length}
        next={fetchData}
        hasMore={articles.length !== totalResults}
        loader={<Spinner />}
      >
        <div className="container my-2">
          <div className="row my-3 ">
            {articles.map((element) => {
              return (
                <div className="col-md-4 my-3" key={element.url}>
                  <NewsItem
                    title={element.title ? element.title : ""}
                    description={
                      element.description
                        ? element.description.slice(0, 200)
                        : ""
                    }
                    newsUrl={element.url}
                    imageUrl={
                      element.urlToImage
                        ? element.urlToImage
                        : "https://thumbs.dreamstime.com/b/newspaper-wooden-table-93401143.jpg"
                    }
                    author={element.author ? element.author : "Unknown"}
                    date={element.publishedAt}
                    source={element.source.name}
                  />
                </div>
              );
            })}
          </div>
        </div>
      </InfiniteScroll>
    </>
  );
};
 export default News
